import React, { Component } from 'react';
import {Link} from 'react-router-dom';
import Admin from '../../../assets/images/admin.jpg';

class SideBar extends Component {
    render() {
        return (
            <>
                <div className="max-w-sm flex-grow min-h-screen pl-16 pt-16 pb-16 text-left bg-gray-100 work-sans">
                    <div className="sideBar relative h-100 pb-24">
                        <div className="sideBar-top pr-8 mb-16">
                            <div className="user-info relative w-full flex items-center pr-8 mb-24">
                                <div className="user-dp">
                                    <img src={Admin} alt="admin" />
                                </div>

                                <div className="user-name pl-2">
                                    <p className="text-sm mb-0 text-gray-800">Hello,
                                        <Link to="#" className="block text-gray-900 font-bold text-lg">Xenon Store</Link>
                                    </p>
                                </div>
                            </div>
                        </div>
                    </div>
                </div>
            </>
        )
    }
}

export default SideBar;
